/**
 * Helper function to get optimal lowpass filter by cutoff
 * @param cutoffNormalized Normalized cutoff frequency (0-0.5)
 * @returns Optimal FIR coefficients
 */
export function getOptimalLowpass(cutoffNormalized: number): Float32Array {
  if (cutoffNormalized <= 0.15) return OPTIMAL_LOWPASS_COEFFS.cutoff_0_1;
  if (cutoffNormalized <= 0.25) return OPTIMAL_LOWPASS_COEFFS.cutoff_0_2;
  if (cutoffNormalized <= 0.35) return OPTIMAL_LOWPASS_COEFFS.cutoff_0_3;
  return OPTIMAL_LOWPASS_COEFFS.cutoff_0_4;
}

/**
 * Helper function to get optimal highpass filter by cutoff
 * @param cutoffNormalized Normalized cutoff frequency (0-0.5)
 * @returns Optimal FIR coefficients
 */
export function getOptimalHighpass(cutoffNormalized: number): Float32Array {
  if (cutoffNormalized <= 0.15) return OPTIMAL_HIGHPASS_COEFFS.cutoff_0_1;
  if (cutoffNormalized <= 0.25) return OPTIMAL_HIGHPASS_COEFFS.cutoff_0_2;
  return OPTIMAL_HIGHPASS_COEFFS.cutoff_0_3;
}

/**
 * Helper function to get optimal bandpass filter
 * @param lowNormalized Lower band edge (normalized, 0-0.5)
 * @param highNormalized Upper band edge (normalized, 0-0.5)
 * @returns Optimal FIR coefficients
 */
export function getOptimalBandpass(
  lowNormalized: number,
  highNormalized: number
): Float32Array {
  const bandwidth = highNormalized - lowNormalized;

  if (bandwidth <= 0.22) return OPTIMAL_BANDPASS_COEFFS.band_0_15_0_35;
  if (bandwidth <= 0.25) return OPTIMAL_BANDPASS_COEFFS.band_0_2_0_4;
  return OPTIMAL_BANDPASS_COEFFS.band_0_25_0_45;
}

/**
 * Get notch filter for power line interference
 * @param sampleRate Sampling rate in Hz
 * @param lineFreq Power line frequency (50 or 60 Hz)
 * @returns Optimal notch filter coefficients
 */
export function getPowerLineNotch(
  sampleRate: number,
  lineFreq: 50 | 60 = 60
): Float32Array {
  if (sampleRate === 1000) {
    return lineFreq === 60
      ? OPTIMAL_NOTCH_COEFFS["60_hz_notch_at_1000_hz_sampling"]
      : OPTIMAL_NOTCH_COEFFS["50_hz_notch_at_1000_hz_sampling"];
  } else if (sampleRate === 500 && lineFreq === 60) {
    return OPTIMAL_NOTCH_COEFFS["60_hz_notch_at_500_hz_sampling"];
  }

  throw new Error(
    `No pre-computed notch filter for ${sampleRate} Hz / ${lineFreq} Hz. Use custom design.`
  );
}

/**
 * Example usage:
 *
 * ```typescript
 * import { FirFilter } from 'dsp-ts-redis';
 * import { OPTIMAL_LOWPASS_COEFFS, getOptimalLowpass } from 'dsp-ts-redis/optimal-fir-tables';
 *
 * // Method 1: Direct access
 * const filter1 = new FirFilter(OPTIMAL_LOWPASS_COEFFS.cutoff_0_2, true);
 *
 * // Method 2: Helper function
 * const coeffs = getOptimalLowpass(0.2);
 * const filter2 = new FirFilter(coeffs, true);
 *
 * // Method 3: Power line notch
 * const notch = new FirFilter(getPowerLineNotch(1000, 60), true);
 *
 * // All filters are 30-50% faster than window-based designs!
 * const output = filter1.process(signal);
 * ```
 */
